INSERT [dbo].[product] ([product_id], [name], [price], [description], [description_detail], [image], [thumb_image], [last_update], [category_id], [quantity]) VALUES (N'birici0001', N'Loa Birici MX-700 450W', CAST(23680000 AS Numeric(18, 0)), N'Thiết kế chắc chắn, cứng cáp với dạng hình chữ nhật. Bánh xe có khóa dễ dàng di chuyển và cố định. Công suất mạnh mẽ Loa 2 đường tiếng sở hữu tổng công suất cao lên tới 450 W, 2 loa Bass đường kính 40 cm, 2 loa Treble cho âm thanh loa điện phát ra trung thực, vang xa, cực đã tai.', N'Tổng công suất:450 W. Thời gian sử dụng:Cắm điện dùng, không có pin Kết nối không dây. Bluetooth Kết nối khác:Jack 6.5 Micro, Jack bông sen trắng đỏ, Thẻ nhớ, USB Tiện ích:Bánh xe dễ di chuyển, Có tay kéo, Nghe được FM Radio, Có kèm remote.  Năm sản xuất:2018', N'birici0001a.jpg', NULL, CAST(N'2019-12-03' AS Date), N'2', NULL)
INSERT [dbo].[product] ([product_id], [name], [price], [description], [description_detail], [image], [thumb_image], [last_update], [category_id], [quantity]) VALUES (N'dell0001', N'Dell Vostro 5568', CAST(21560000 AS Numeric(18, 0)), N'Dell Vostro 5581- 93S1GT2 là chiếc máy tính văn phòng đáng sở hữu với mức giá tốt. Máy sở hữu thiết kế đơn giản nhưng không kém phần sang trọng cùng một cấu hình mạnh mẽ đáp ứng tốt nhu cầu văn phòng và giải trí thường ngày. Cấu hình mạnh mẽ, chạy được các ứng dụng nặng Laptop được trang bị chip Intel Core i5 thế hệ 8 kết hợp với RAM 4 GB đem đến sức mạnh đủ để chạy mượt mà các ứng dụng văn phòng hiện hành. Các phần mềm thiết kế như Photoshop, CoreDraw máy cũng có thể xử lý với các tác vụ cắt ghép, chỉnh sửa vừa phải. Thiết kế đơn giản, cứng cáp Laptop Dell Vostro 5581 sở hữu thiết kế đơn giản đặc trưng của các dòng laptop Dell. Vỏ ngoài chắc chắn, đường nét toát lên sự sang trọng và cứng cáp. Các góc máy được bo tròn nhẹ dễ dàng cầm nắm. Độ dày 20,3 mm và nặng 1,9 kg tương đối dễ dàng trong việc di chuyển.', N'CPU: Intel Core i5 Coffee Lake, 8265U, 1.60 GHz. RAM: 4 GB, DDR4 (2 khe), 2666 MHz Ổ cứng: HDD: 1 TB SATA3, Hỗ trợ khe cắm SSD M.2 PCIe. Màn hình: 15.6 inch, Full HD (1920 x 1080). Card màn hình: Card đồ họa tích hợp, Intel® UHD Graphics 620. Cổng kết nối: 2 x USB 3.1, HDMI, LAN (RJ45), USB 2.0, USB Type-C. Hệ điều hành: Windows 10 + Office 365 1 năm. Thiết kế: Vỏ nhựa, PIN liền. Kích thước: Dày 20.3 mm, 1.9 kg.', N'dell0001a.jpg', NULL, CAST(N'2019-12-03' AS Date), N'3', NULL)
INSERT [dbo].[product] ([product_id], [name], [price], [description], [description_detail], [image], [thumb_image], [last_update], [category_id], [quantity]) VALUES (N'dell0002', N'Dell XPS 13 9370 i7 8550U', CAST(34860000 AS Numeric(18, 0)), N'Nếu bạn đang tìm kiếm một chiếc laptop mỏng nhẹ dễ dàng di chuyển nhưng vẫn sở hữu một cấu hình mạnh thì laptop Dell XPS 13 9370 chính là lựa chọn hợp lí. Dell XPS 13 được thiết kế siêu mỏng nhẹ, bắt mắt, được trang bị chip Core i7 mạnh mẽ cùng nhiều tính năng hiện đại. CPU Intel Core i7 Coffee Lake thế hệ thứ 8 đem đến hiệu năng mạnh mẽ giúp bạn giải quyết tốt mọi công việc với chiếc máy này. RAM 8 GB đa nhiệm nhanh chóng, thiết kế đồ họa mượt mà. Laptop siêu mỏng nhẹ với vỏ kim loại nguyên khối, mỏng chỉ 11.6 mm, trọng lượng 1.21 kg dễ dàng mang theo đến bất cứ đâu.', N'CPU: Intel Core i7 Coffee Lake, 8550U, 1.80 GHz. RAM: 8 GB, DDR3L(On board), 1866 MHz. Ổ cứng: SSD 256GB NVMe PCIe. Màn hình: 13.3 inch, Full HD (1920 x 1080). Card màn hình: Card đồ họa tích hợp, Intel® UHD Graphics 620. Cổng kết nối: 2 x Thunderbolt 3 (USB-C), USB Type-C. Hệ điều hành: Windows 10 + Office 365 1 năm. Thiết kế: Vỏ kim loại nguyên khối. PIN liền. Kích thước: Dày 11.6 mm, 1.21 kg', N'dell0002a.jpg', NULL, CAST(N'2019-12-03' AS Date), N'3', NULL)
INSERT [dbo].[product] ([product_id], [name], [price], [description], [description_detail], [image], [thumb_image], [last_update], [category_id], [quantity]) VALUES (N'hitachisu0001', N'Máy hút bụi Hitachi SU 21V', CAST(3860000 AS Numeric(18, 0)), N'Máy Hút Bụi Hitachi SU 21V với dung tích 1,6 lít, máy hút sẽ giúp bạn làm việc trong thời gian dài. Máy có thiết kế nhở gọn và cơ chế hoạt động đơn giản giúp bạn dễ dàng hơn với các thao tác khi sử dụng. Với nhiều đầu hút đi kèm có thể linh động tháo lắp sẽ hỗ trợ bạn tối đa trong công việc làm sạch nhà cửa cũng như chùi rửa, vệ sinh máy móc. Máy hút bụi Hitachi là sự lựa chọn lý tưởng dành cho chị em phụ nữ trong cuộc sống hiện đại và vội vã như ngày nay.', N'Thương hiệu: Hitachi . Sản xuất tại: Thái Lan. Model: SU 21V. Dung tích: 1.6 lít. Điện áp: 220 - 240V,50~60Hz. Kích thước: 389x278x244mm', N'hitachisu0001a.jpg', NULL, CAST(N'2019-12-03' AS Date), N'4', NULL)
INSERT [dbo].[product] ([product_id], [name], [price], [description], [description_detail], [image], [thumb_image], [last_update], [category_id], [quantity]) VALUES (N'hp0001', N'HP 15 da1031TX', CAST(14000000 AS Numeric(18, 0)), N'HP 15 da1031TX là mẫu laptop văn phòng có thiết kế thân thiện, thanh lịch và cấu hình mạnh mẽ, phù hợp xử lý văn phòng từ cơ bản đến nâng cao. Những thông số đáng chú ý khác là card đồ họa rời, khe cắm SSD và ổ đĩa quang tiện lợi. Thiết kế đơn giản, thanh lịch. Theo xu hướng thanh lịch và thân thiện, laptop HP 15 da1031TX phù hợp với những cá nhân thích thiết kế đơn giản nhưng vẫn khá sang trọng. Tuy lớp vỏ được gia công từ nhựa nhưng vẫn đảm bảo về chất lượng độ bền. Cấu hình mạnh mẽ, có thể nâng cấp SSD. Bạn có thể nâng cấp thêm cho 4 GB RAM giữ mức độ đa nhiệm ổn định, chạy cùng lúc 20 tab Chrome hay vài ứng dụng cùng lúc mà máy không bị đứng. Card đồ họa rời NVIDIA GeForce MX110 2 GB, Chip Intel Core i5 thế hệ 8 mạnh mẽ đáp ứng hết mọi nhu cầu sử dụng thường ngày như lướt web, xem phim, soạn thảo văn bản,... Xử lý đồ họa chuyên nghiệp với Photoshop, Corel hay chơi các game các hình trung bình như DOTA, LOL,... với những hình ảnh mượt mà nhất, không còn tình trạng xé hay giựt hình khi đang chiến game. Màn hình 15,6 inch cùng công nghệ chống chói hiển thị rõ ràng mọi hình ảnh kể cả khi bạn làm việc dưới ánh nắng trong quán cà phê. Laptop có pin 3 cell, thời lượng sử dụng thực tế khoảng 5 giờ đồng hồ, đủ dùng để học tập và làm việc, đem theo ra ngoài.', N'CPU: Intel Core i5 Coffee Lake, 8265U, 1.60 GHz. RAM: 4 GB, DDR4 (2 khe), 2133 MHz. Ổ cứng: HDD: 1 TB SATA3, Hỗ trợ khe cắm SSD M.2 PCIe. Màn hình: 15.6 inch, HD (1366 x 768). Card màn hình: Card đồ họa rời, NVIDIA GeForce MX110, 2GB. Cổng kết nối: HDMI 1.4, 2 x USB 3.0, LAN (RJ45), USB 2.0. Hệ điều hành: Windows 10 Home SL. Thiết kế: Vỏ nhựa, PIN liền. Kích thước: Dày 22.5 mm, 1.77 kg.', N'hp0001a.jpg', NULL, CAST(N'2019-12-04' AS Date), N'3', 0)
INSERT [dbo].[product] ([product_id], [name], [price], [description], [description_detail], [image], [thumb_image], [last_update], [category_id], [quantity]) VALUES (N'hp0002', N'HP Pavilion 14 ce1018TU', CAST(15680000 AS Numeric(18, 0)), N'Thiết kế trang nhã, khá nhẹ giúp thuận tiện cho việc di chuyển. HP Pavilion 14 (5RL41PA) được gia công tỉ mỉ, hoàn thiện từ chất liệu nhựa với 4 góc cạnh được bo tròn mang đến vẻ đẹp thanh lịch, trang nhã. Bên cạnh đó, với trọng lượng chỉ 1.59 Kg giúp cho bạn không cảm thấy mệt mỏi khi di chuyển cùng chiếc laptop văn phòng HP. Hình ảnh sống động, chân thật đến từng chi tiết. Chiếc laptop HP được trang bị màn hình có kích thước 14 inch với  độ phân giải Full HD cho trải nghiệm hình ảnh chân thật, sắc nét đến từng chi tiết kết hợp với tấm nền IPS giúp mở rộng góc nhìn. Công nghệ Led Backlit giúp cho màn hình không tiêu tốn quá nhiều điện năng khi sử dụng. Giờ đây, việc thưởng thức những thước phim hay cùng bạn bè, đồng nghiệp sau những giờ học tập và làm việc sẽ dễ dàng hơn bao giờ hết. Cấu hình khá khỏe, xử lý tốt các thao tác đồ họa cơ bản Được trang bị chip xử lý mạnh mẽ đến từ core i5-8265U kết hợp cùng 4GB Ram DDR4 giúp chạy mượt mà các ứng dụng văn phòng cũng như xử lý tốt các thao tác đồ họa cơ bản trong photoshop, ai, corel.... .Hỗ trợ truyền dữ liệu nhanh chóng qua cổng USB 3.1 và USB Type-C. Ngoài việc trang bị khá đầy đủ các cổng kết nối cơ bản, sản phẩm còn được trang bị thêm 2 cổng USB 3.1 và 1 cổng USB Type-C giúp cho việc truyền dữ liệu trở nên nhanh chóng với tốc độ tối đa là 600Mb/s.', N'CPU: Intel Core i5 Coffee Lake, 8265U, 1.60 GHz. RAM: 4 GB, DDR4 (1 khe), 2400 MHz. Ổ cứng: HDD: 1 TB, Intel Optane 16GB, (Hỗ trợ thay thế SSD PCIe hoặc M.2 SATA 3). Màn hình: 14 inch, Full HD (1920 x 1080). Card màn hình: Card đồ họa tích hợp, Intel® HD Graphics 620. Cổng kết nối: 2 x USB 3.1, HDMI, LAN (RJ45), USB Type-C. Hệ điều hành: Windows 10 Home SL. Thiết kế: Vỏ nhựa, PIN liền. Kích thước: Dày 17 mm, 1.59 kg.', N'hp0002a.jpg', NULL, CAST(N'2019-12-06' AS Date), N'3', NULL)
INSERT [dbo].[product] ([product_id], [name], [price], [description], [description_detail], [image], [thumb_image], [last_update], [category_id], [quantity]) VALUES (N'iphone0001', N'Iphone 11', CAST(33990000 AS Numeric(18, 0)), N'Camera được cải tiến mạnh mẽ  với 3 camera ở mặt sau và cả 3 camera này đều có độ phân giải là 12 MP. Chiếc iPhone mới này chạy trên con chip Apple A13 Bionic, con chip mạnh mẽ nhất dành cho những chiếc iPhone trong năm 2019. Bên cạnh đó bạn vẫn sẽ có cho mình 4 GB RAM và 64 GB bộ nhớ trong thoải mái cho bạn cài đặt game và ứng dụng.', N'Màn hình: OLED, 6.5", Super Retina XDR. Hệ điều hành: iOS 13. Camera sau: 3camera 12 MP. Camera trước: 12 MP. CPU: Apple A13 Bionic 6 nhân. RAM: 4 GB. Bộ nhớ trong: 64 GB', N'iphone0001a.jpg', NULL, CAST(N'2019-12-03' AS Date), N'1', NULL)
INSERT [dbo].[product] ([product_id], [name], [price], [description], [description_detail], [image], [thumb_image], [last_update], [category_id], [quantity]) VALUES (N'iphone0002', N'Iphone X', CAST(16800000 AS Numeric(18, 0)), N'Màn hình tràn viền công nghệ LCD - True Tone.Thay vì sở hữu màn hình OLED truyền thống, chiếc smartphone này sở hữu màn hình LCD. Mượt mà mọi trải nghiệm nhờ chip Apple A12 .Với mỗi lần ra mắt, Apple lại giới thiệu một con chip mới và Apple A12 Bionic là con chip đầu tiên sản xuất với tiến trình 7nm được tích hợp trên iPhone Xr.Camera với hiệu ứng bokeh cực đẹp.Nếu ai đó khẳng định chỉ camera kép mới xoá phông thì đó là một sai lầm. iPhone Xr có khả năng tạo hiệu ứng bokeh tuyệt đỉnh, làm nổi bật chân dung người chụp mà hình ảnh vẫn rất sắc nét, chi tiết', N'Màn hình: IPS LCD, 6.1", Liquid Retina. Hệ điều hành: iOS 12. Camera sau: 12 MP. Camera trước: 7MP. CPU: Apple A12 Bionic 6 nhân. RAM: 3 GB. Bộ nhớ trong: 128 GB', N'iphone0002a.jpg', NULL, CAST(N'2019-12-03' AS Date), N'1', NULL)
INSERT [dbo].[product] ([product_id], [name], [price], [description], [description_detail], [image], [thumb_image], [last_update], [category_id], [quantity]) VALUES (N'iphone0003', N'iPhone 8 Plus', CAST(14990000 AS Numeric(18, 0)), N'Thừa hưởng những thiết kế đã đạt đến độ chuẩn mực, thế hệ iPhone 8 Plus thay đổi phong cách bóng bẩy hơn và bổ sung hàng loạt tính năng cao cấp cho trải nghiệm sử dụng vô cùng tuyệt vời. IPhone 8 Plus giữ nguyên hoàn toàn những đường nét thiết kế đã hoàn thiện từ thế hệ trước nhưng sử dụng phong cách 2 mặt kính cường lực kết hợp bộ khung kim loại. Một trong những điểm nhất quan trọng nhất ở thế hệ iPhone 8 Plus chính là sức mạnh phần cứng, con chip Apple A11 như trên iPhone X cho hiệu năng vượt trội hơn rất nhiều so với trước đây. Nhờ thiết kế mặt lưng kính mà iPhone 8 Plus đã có thể tích hợp thêm công nghệ sạc không dây tiên tiến cho sử dụng an toàn và thuận tiện hơn rất nhiều.', N'Màn hình: LED-backlit IPS LCD, 5.5", Retina HD. Hệ điều hành: iOS 12. Camera sau: Chính 12 MP & Phụ 12 MP. Camera trước: 7 MP. CPU: Apple A11 Bionic 6 nhân. RAM: 3 GB. Bộ nhớ trong: 64 GB', N'iphone0003a.jpg', NULL, CAST(N'2019-12-03' AS Date), N'1', NULL)
INSERT [dbo].[product] ([product_id], [name], [price], [description], [description_detail], [image], [thumb_image], [last_update], [category_id], [quantity]) VALUES (N'iphone0004', N'Iphone 7 Plus', CAST(11990000 AS Numeric(18, 0)), N'Mặc dù giữ nguyên vẻ bề ngoài so với dòng điện thoại iPhone đời trước, bù lại iPhone 7 Plus 32GB lại được trang bị nhiều nâng cấp đáng giá như camera kép đầu tiên cũng như cấu hình mạnh mẽ. Chiếc điện thoại sở hữu camera kép đầu tiên của Apple iPhone 7 Plus là chiếc iPhone đầu tiên được trang bị camera kép có cùng độ phân giải 12 MP, đem lại khả năng chụp ảnh ở hai tiêu cự khác nhau. Cấu hình mạnh mẽ với chip Apple A10. Ngoài trái tim Apple A10 Fusion 4 nhân với hiệu năng cực kì mạnh mẽ và ấn tượng thì iPhone 7 Plus còn được cập nhật hệ điều hành mới nhất với nhiều tính năng bất ngờ, và thú vị. Màn hình Retina trên 7 Plus hỗ trợ DCI-P3 gam màu rộng, nghĩa là chúng có khả năng tái tạo màu sắc trong phạm vi của sRGB. Điểm nhấn ấn tượng nhất nằm ở mặt lưng của sản phẩm với hiệu ứng phản chiếu ánh sáng khi thay đổi góc nhìn rất đặc biệt. Hiệu ứng này cũng được áp dụng cho toàn bộ khung máy, từ cạnh trên, cạnh dưới cho đến các nút bấm nên cho cảm giác một thiết bị cực kỳ hoàn thiện, gần như không có bất kỳ một chi tiết thừa nào cả.', N'Màn hình: LED-backlit IPS LCD, 5.5", Retina HD. Hệ điều hành: iOS 12. Camera sau: Chính 12 MP & Phụ 12 MP. Camera trước: 7 MP. CPU: Apple A10 Fusion 4 nhân. RAM: 3 GB. Bộ nhớ trong: 32 GB', N'iphone0004a.jpg', NULL, CAST(N'2019-12-03' AS Date), N'1', NULL)
